	// recrods the stat of a bool vector: either all true, none true, or a mixture of true and false.
	all; 
	none;
	mixture;
	}

	logical_summary make_logical_summary(bool[] X){
   		if(all(X))
   			return all;
   		else if(none(X))
   			return none;
   		else
   			return mixture;
	}

